mapper.select:
select
  id,
  type_int,
  type_bigint,
  type_binary,
  type_bit,
  type_blob,
  type_char,
  type_date,
  type_datetime,
  type_decimal,
  type_double,
  type_float,
  type_geometry,
  type_geometrycollection,
  type_integer,
  type_json,
  type_linestring,
  type_longblob,
  type_longtext,
  type_mediumblob,
  type_mediumint,
  type_mediumtext,
  type_multilinestring,
  type_multipoint,
  type_multipolygon,
  type_numeric,
  type_point,
  type_polygon,
  type_real,
  type_text,
  type_time,
  type_timestamp,
  type_tinyblob,
  type_tinyint,
  type_tinytext,
  type_varbinary,
  type_varchar,
  type_year,
  'true' as QUERYID
FROM
  demo_types
WHERE
  1 = 1

    AND id = #{id}


    AND type_int = #{typeInt}


    AND type_bigint = #{typeBigint}


    AND type_binary = #{typeBinary}


    AND type_bit = #{typeBit}


    AND type_blob = #{typeBlob}


    AND type_char = #{typeChar}


    AND type_date = #{typeDate}


    AND type_datetime = #{typeDatetime}


    AND type_decimal = #{typeDecimal}


    AND type_double = #{typeDouble}


    AND type_float = #{typeFloat}


    AND type_geometry = #{typeGeometry}


    AND type_geometrycollection = #{typeGeometrycollection}


    AND type_integer = #{typeInteger}


    AND type_json = #{typeJson}


    AND type_linestring = #{typeLinestring}


    AND type_longblob = #{typeLongblob}


    AND type_longtext = #{typeLongtext}


    AND type_mediumblob = #{typeMediumblob}


    AND type_mediumint = #{typeMediumint}


    AND type_mediumtext = #{typeMediumtext}


    AND type_multilinestring = #{typeMultilinestring}


    AND type_multipoint = #{typeMultipoint}


    AND type_multipolygon = #{typeMultipolygon}


    AND type_numeric = #{typeNumeric}


    AND type_point = #{typePoint}


    AND type_polygon = #{typePolygon}


    AND type_real = #{typeReal}


    AND type_text = #{typeText}


    AND type_time = #{typeTime}


    AND type_timestamp = #{typeTimestamp}


    AND type_tinyblob = #{typeTinyblob}


    AND type_tinyint = #{typeTinyint}


    AND type_tinytext = #{typeTinytext}


    AND type_varbinary = #{typeVarbinary}


    AND type_varchar = #{typeVarchar}


    AND type_year = #{typeYear}
